package tryHelloWorld;

public class Pos {

	private int x;
	private int y;
	private int level;
	
	public Pos() {
		// TODO Auto-generated constructor stub
	}
	public Pos(int x, int y, int level) {
		this.x = x;
		this.y = y;
		this.level = level;
	}
	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}

	public int getLevel() {
		return level;
	}

	public void setLevel(int level) {
		this.level = level;
	}
}
